import matplotlib.pyplot as plt
import numpy as np
import seaborn as sns
from matplotlib.animation import FuncAnimation

num_states = len(states)

# Prepare figure and aesthetics
plt.style.use('dark_background')

fig, ax = plt.subplots(figsize=(8, 6))
fig.patch.set_facecolor('black')  # ensure figure background is black

# Customize axes
ax.set_facecolor('black')
ax.spines['bottom'].set_color('white')
ax.spines['top'].set_color('white')
ax.spines['left'].set_color('white')
ax.spines['right'].set_color('white')
ax.tick_params(axis='x', colors='white')
ax.tick_params(axis='y', colors='white')
ax.set_xscale('log')
ax.set_yscale('log')

# We will update these objects in the animation
hist_container = None
kde_line = None

def update(frame):
    global hist_container, kde_line
    ax.clear()

    # Re-apply styling after clearing
    ax.set_facecolor('black')
    for spine in ax.spines.values():
        spine.set_color('white')
    ax.tick_params(axis='x', colors='white')
    ax.tick_params(axis='y', colors='white')
    ax.set_xscale('log')
    ax.set_yscale('log')

    # Extract data from the state dict
    state_dict = states[frame]
    x_data = np.array(list(state_dict.keys()))
    y_data = np.array(list(state_dict.values()))


    # Filter out non-positive values, since log scale requires > 0
    mask = (x_data > 0) & (y_data > 0)
    x_data = x_data[mask]
    y_data = y_data[mask]

    # Sort by x so bars appear in ascending order
    sort_idx = np.argsort(x_data)
    x_data = x_data[sort_idx]
    y_data = y_data[sort_idx]

    # Plot bars
    # Adjust width if needed. On a log scale, a uniform width may not appear uniform.
    # Start with something small (e.g., width=0.1 * smallest gap).
    # If x_data is dense, you can try something else.
    # if len(x_data) > 1:
    #     # Estimate a width based on gaps in log space:
    #     # We'll do something simple like a small fraction of the median log spacing.
    #     log_x = np.log10(x_data)
    #     spacings = np.diff(log_x)
    #     median_spacing = np.median(spacings) if len(spacings) > 0 else 0.1
    #     width = 10**(median_spacing * 0.3) - 10**0  # heuristic
    # else:
    #     width = 0.1

    width = x_data * 0.01
    ax.bar(x_data, y_data, width=width, color='#00ffd0', edgecolor='none')


    # Set labels/title
    ax.set_xlabel('Stone ID', color='white', fontsize=12)
    ax.set_ylabel('Stone Count', color='white', fontsize=12)
    ax.set_title(f'Blink {frame}', color='white', fontsize=14)

    return []

# Create the animation
ani = FuncAnimation(fig, update, frames=num_states, interval=300, blit=False, repeat=False)

